public class DmDatabaseTable {

	/*数据库表名*/
	private String table;
	/*数据表类型*/
	private String tableType;
	/*数据表类型*/
	private String status;
	/*数据表注释*/
	private String comment;
	
	/** default constructor */
	public DmDatabaseTable() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	/** full constructor */
	public DmDatabaseTable(String table, String tableType, String status,
			String comment) {
		super();
		this.table = table;
		this.tableType = tableType;
		this.status = status;
		this.comment = comment;
	}
	
	public DmDatabaseTable(Object table, Object tableType, Object status,Object comment) {
		super();
		this.table = String.valueOf(table);
		this.tableType = String.valueOf(tableType);
		this.status = String.valueOf(status);
		this.comment = String.valueOf(comment);
	}

	public String getTable() {
		return table;
	}

	public void setTable(String table) {
		this.table = table;
	}

	public String getTableType() {
		return tableType;
	}

	public void setTableType(String tableType) {
		this.tableType = tableType;
	}

	public String getStatus() {
		return status;
	}

	public void setStatus(String status) {
		this.status = status;
	}

	public String getComment() {
		return comment;
	}

	public void setComment(String comment) {
		this.comment = comment;
	}
	
}


